SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET AUTOCOMMIT = 0;
START TRANSACTION;

DELIMITER $$
CREATE PROCEDURE `asm_entities_p_get_items` (IN `p_company_id` INT, IN `p_type_id` INT, IN `p_page` INT, IN `p_limit` INT)  NO SQL
BEGIN
SET SESSION group_concat_max_len = 1000000;

select e.id, e.show_date showDate, e.user_id userId, e.author_id authorId, 
       e.main_image mainImage, e.main_image_type mainImageType, e.main_image_noext mainImageNoExt,
       e.is_visible isVisible, e.is_archive isArchive, e.is_important isImportant, 
       'lang||link||urlName||seoKeywords||seoDescription||title||shortDescription||body' rawTranslationsHeader,
       
       GROUP_CONCAT(
       concat(l.lang, '||', COALESCE(d.link,''), '||', COALESCE(d.url_name,''), '||', COALESCE(d.seo_keywords,''), '||', 
       COALESCE(d.seo_description,''), '||', COALESCE(d.title,''), '||', 
       COALESCE(d.short_description, ''), '||', COALESCE(d.body,'')) 
           ORDER BY l.lang SEPARATOR '&&') rawTranslations
  from asm_entities e
  join asm_company_languages c on c.company_id=e.company_id and c.is_active=1
  join asm_languages l on l.id=c.lang_id
  left join asm_entity_details d on d.entity_id=e.id and d.lang=l.lang
 where e.company_id=p_company_id and e.type_id=p_type_id and e.is_active=1
 group by e.id, e.show_date, e.main_image, e.main_image_type, e.main_image_noext,
       e.is_visible, e.is_archive, e.is_important, e.user_id, e.author_id
 order by e.show_date desc, e.id desc
 limit p_page, p_limit;
END$$

CREATE PROCEDURE `asm_entities_p_get_item_by_id` (IN `p_company_id` INT, IN `p_item_id` INT)  NO SQL
select e.id, e.show_date showDate, e.user_id userId, e.author_id authorId, 
       e.main_image mainImage, e.main_image_type mainImageType, e.main_image_noext mainImageNoExt,
       e.is_visible isVisible, e.is_archive isArchive, e.is_important isImportant, 
       'lang||link||urlName||seoKeywords||seoDescription||title||shortDescription||body' rawTranslationsHeader,       
       GROUP_CONCAT(
       concat(l.lang, '||', COALESCE(d.link,''), '||', COALESCE(d.url_name,''), '||', COALESCE(d.seo_keywords,''), '||', 
       COALESCE(d.seo_description,''), '||', COALESCE(d.title,''), '||', 
       COALESCE(d.short_description, ''), '||', COALESCE(d.body,'')) 
           ORDER BY l.lang SEPARATOR '&&') rawTranslations
  from asm_entities e
  join asm_company_languages c on c.company_id=e.company_id and c.is_active=1
  join asm_languages l on l.id=c.lang_id
  left join asm_entity_details d on d.entity_id=e.id and d.lang=l.lang
 where e.company_id=p_company_id and e.id=p_item_id and e.is_active=1
 group by e.id, e.show_date, e.main_image, e.main_image_type, e.main_image_noext,
       e.is_visible, e.is_archive, e.is_important, e.user_id, e.author_id$$

CREATE PROCEDURE `asm_entities_p_get_item_count` (IN `p_company_id` INT, IN `p_type_id` INT)  NO SQL
select count(0) itemCount
