CREATE PROCEDURE `asm_entities_p_get_item_images` (IN `p_company_id` INT, IN `p_item_id` INT)  NO SQL
select i.id, i.image_file imageFile, i.image_type imageType,
       i.image_file_noext imageFileNoExt, i.is_visible isVisible
  from asm_entities e 
  join asm_entity_images i on i.entity_id=e.id and i.is_active=1
 where e.company_id=p_company_id and e.id = p_item_id and e.is_active=1 
