CREATE PROCEDURE `asm_entities_p_get_type` (IN `p_company_id` INT, IN `p_entity_type` VARCHAR(100))  NO SQL
select t.id, t.name, t.main_image mainImage, t.main_image_type mainIMageType, t.main_image_noext mainImageNoExt,
       t.multiimage, t.has_tags hasTags, t.has_title hasTitle, t.has_short_description hasShortDescription,
       t.has_global_seo hasGlobalSeo, t.has_link hasLink, t.has_seo hasSeo, t.can_create canCreateItem,
       t.has_body hasBody, t.has_archive hasArchive, t.is_visible isVisible, t.has_date hasDate,
       'lang||h1Title||seoTitle||shortDescription||body||urlName||seoKeywords||seoDescription' rawParamsHeader,
       GROUP_CONCAT(
       concat(l.lang, '||', COALESCE(d.h1_title,''), '||', COALESCE(d.title,''), '||', 
              COALESCE(d.short_description,''), '||',  COALESCE(d.body,''), '||', 
              COALESCE(d.url_name,''), '||', COALESCE(d.seo_keywords,''), '||', 
              COALESCE(d.seo_description,''))
           ORDER BY l.lang SEPARATOR '&&') rawParams
  from asm_entity_types t
  join asm_company_languages c on c.company_id=t.company_id and c.is_active=1
  join asm_languages l on l.id=c.lang_id
  left join asm_entity_type_details d on t.id=d.type_id and d.lang=l.lang 
 where t.company_id=p_company_id and t.name=p_entity_type and t.is_active=1
 group by t.id, t.name, t.main_image, t.main_image_type, t.main_image_noext,
       t.multiimage, t.has_tags, t.has_title, t.has_short_description,
       t.has_body, t.has_archive, t.is_visible$$

CREATE PROCEDURE `asm_entities_p_move_image` (IN `p_company_id` INT, IN `p_item_id` INT, IN `p_image_id` INT, IN `p_sign` INT)  NO SQL
BEGIN
  DECLARE l_curr_priority int;
  DECLARE l_next_id int;
  DECLARE l_next_priority int;
  
  select sort_order
    into l_curr_priority
    from asm_entity_images
   where id = p_image_id and entity_id=p_item_id ;
     
  select id, sort_order
    into l_next_id, l_next_priority
    from asm_entity_images m
   where m.entity_id=p_item_id 
     and (p_sign*m.sort_order>p_sign*l_curr_priority)
   order by p_sign*m.sort_order
   limit 1;
   
  update asm_entity_images set sort_order=l_next_priority where id=p_image_id;
  update asm_entity_images set sort_order=l_curr_priority where id=l_next_id; 
  
  call asm_entities_p_get_item_by_id(p_company_id, p_item_id);
END$$

CREATE PROCEDURE `asm_entities_p_save_entity` (IN `p_company_id` INT, IN `p_entity` VARCHAR(100), IN `p_values` TEXT CHARSET utf8mb4)  NO SQL
BEGIN
  DECLARE l_id int;
  
  SELECT t.id
    INTO l_id
    FROM asm_entity_types t
   WHERE t.company_id=p_company_id and t.name=p_entity and t.is_active=1; 
  
  select l_id, 
         asm_f_extract_string(0, val, '||'), 
         asm_f_extract_string(1, val, '||'), 
         asm_f_extract_string(2, val, '||'), 
         asm_f_extract_string(3, val, '||'), 
         asm_f_extract_string(4, val, '||')
  from (
  select asm_f_extract_string(n, p_values, '&&') val
    from asm_number_set 
   where n<asm_f_substring_count(p_values,'&&')) x
  on duplicate key update 
      seo_keywords=values(seo_keywords),
      seo_description=values(seo_description),
      title=values(title),
      h1_title=values(h1_title);
      
  call asm_entities_p_get_type(p_company_id, p_entity);
END$$

CREATE PROCEDURE `asm_entities_p_save_item` (IN `p_company_id` INT, IN `p_entity` VARCHAR(100), IN `p_item_id` INT, IN `p_show_date` DATETIME, IN `p_values` TEXT CHARSET utf8mb4)  NO SQL
BEGIN
  DECLARE l_id int;
  
  if p_item_id = 0 then 
    select company_id, id, p_show_date, 0
      from asm_entity_types
     where name=p_entity and company_id=p_company_id; 
    select last_insert_id() into l_id;
  else 
    update asm_entities
       set show_date=p_show_date
     where id=p_item_id and company_id=p_company_id;  
  end if;
  
  select COALESCE(l_id, p_item_id), 
         asm_f_extract_string(0, val, '||'), 
         asm_f_extract_string(1, val, '||'), 
         asm_f_extract_string(2, val, '||'), 
         asm_f_extract_string(3, val, '||'), 
         asm_f_extract_string(4, val, '||'), 
         asm_f_extract_string(5, val, '||'), 
         asm_f_extract_string(6, val, '||'), 
         asm_f_extract_string(7, val, '||')
  from (
  select asm_f_extract_string(n, p_values, '&&') val
    from asm_number_set 
   where n<asm_f_substring_count(p_values,'&&')) x
  on duplicate key update 
      link=values(link),
      url_name=values(url_name),
      seo_keywords=values(seo_keywords),
      seo_description=values(seo_description),
      title=values(title),
      short_description=values(short_description),
      body=values(body);
      
  call asm_entities_p_get_item_by_id(p_company_id, COALESCE(l_id, p_item_id));
END$$

CREATE PROCEDURE `asm_entities_p_toggle_image_visibility` (IN `p_company_id` INT, IN `p_image_id` INT)  NO SQL
BEGIN
  DECLARE l_entity_id int;
  
  select entity_id
    into l_entity_id
    from asm_entity_images
   where id=p_image_id;
   
  update asm_entity_images
     set is_visible = 1 - is_visible
   where id=p_image_id;
   
  call asm_entities_p_get_item_by_id(p_company_id, l_entity_id);
END$$

