CREATE PROCEDURE `asm_system_p_delete_image` (IN `p_company_id` INT, IN `p_object_type` VARCHAR(100), IN `p_object_id` INT)  NO SQL
BEGIN
  DECLARE l_item_id int;
  
  IF p_object_type = 'entity_item_main' THEN
    UPDATE asm_entities
       SET main_image = null,
           main_image_type = null,
           main_image_noext = null
     WHERE company_id = p_company_id
       AND id = p_object_id;
       
    call asm_entities_p_get_item_by_id(p_company_id, p_object_id);
  ELSEIF p_object_type = 'entity_item_secondary' THEN
  
    select entity_id
      into l_item_id
      from asm_entity_images
     WHERE id = p_object_id;
     
    UPDATE asm_entity_images 
       SET is_active=0
     WHERE id = p_object_id;
    
    call asm_entities_p_get_item_by_id(p_company_id, l_item_id);
  ELSEIF p_object_type = 'entity_main' THEN
  
    UPDATE asm_entity_types
       SET main_image = null,
           main_image_type = null,
           main_image_noext = null
     WHERE company_id = p_company_id
       AND id = p_object_id;
       
    select p_object_id id, p_image_file imageFile;
  ELSE
   
    SIGNAL SQLSTATE '21022'
       SET MESSAGE_TEXT = 'NOT_CORRECT_OBJECT', MYSQL_ERRNO = 21022;

  END IF;
  
  
END$$

