CREATE PROCEDURE `asm_system_p_upload_image` (IN `p_company_id` INT, IN `p_object_type` VARCHAR(100), IN `p_object_id` INT, IN `p_image_file` VARCHAR(255), IN `p_image_type` VARCHAR(100))  NO SQL
BEGIN
  DECLARE l_order int;
  
  IF p_object_type = 'entity_item_main' THEN
    UPDATE asm_entities
       SET main_image = p_image_file,
           main_image_type = p_image_type,
           main_image_noext = asm_f_remove_file_ext(p_image_file)
     WHERE company_id = p_company_id
       AND id = p_object_id;
       
    call asm_entities_p_get_item_by_id(p_company_id, p_object_id);
  ELSEIF p_object_type = 'entity_item_secondary' THEN
  
    SELECT COALESCE(max(sort_order),0)+10 INTO l_order
      FROM asm_entity_images
     WHERE entity_id = p_object_id
       AND is_active = 1;
     
    VALUES (p_object_id, p_image_file, p_image_type, asm_f_remove_file_ext(p_image_file), l_order, 0, 1);
    
    call asm_entities_p_get_item_by_id(p_company_id, p_object_id);
  ELSEIF p_object_type = 'entity_main' THEN
  
    UPDATE asm_entity_types
       SET main_image = p_image_file,
           main_image_type = p_image_type,
           main_image_noext = asm_f_remove_file_ext(p_image_file)
     WHERE company_id = p_company_id
       AND id = p_object_id;
       
    select p_object_id id, p_image_file imageFile;
  ELSE
   
    SIGNAL SQLSTATE '21022'
       SET MESSAGE_TEXT = 'NOT_CORRECT_OBJECT', MYSQL_ERRNO = 21022;

  END IF;
  
  
END$$

CREATE PROCEDURE `asm_translations_p_edit` (IN `p_company_id` INT, IN `p_name` VARCHAR(100), IN `p_values` TEXT CHARSET utf8mb4)  NO SQL
BEGIN
select p_company_id, p_name name, asm_f_extract_string(0, val, '||') lang, asm_f_extract_string(1, val, '||') body, 1 type_id, 1
  from (
select asm_f_extract_string(n, p_values, '&&') val
  from asm_number_set 
 where n<asm_f_substring_count(p_values,'&&')) x
 on duplicate key update is_active=1, body=values(body);
 
call asm_translations_p_get_by_name(p_company_id, p_name);
END$$

CREATE PROCEDURE `asm_translations_p_get_by_name` (IN `p_company_id` INT, IN `p_name` VARCHAR(100))  NO SQL
select o.name, o.description,
       'id||lang||body' rawTranslationsHeader,
       GROUP_CONCAT(distinct concat(COALESCE(r.id,0), '||', l.lang, '||', COALESCE(r.body,'')) ORDER by l.lang SEPARATOR '&&') rawTranslations
  from 
      (select t.name, max(t.description) description, t.company_id
  from asm_common_translations t
 where t.type_id=1 and t.is_active=1 and t.company_id=p_company_id
   and t.name =p_name
      group by t.company_id, t.name) o
  join asm_company_languages c on c.company_id=o.company_id
  join asm_languages l on l.id=c.lang_id
  left join asm_common_translations r on r.name=o.name and r.company_id=o.company_id and r.lang=l.lang and r.is_active=1
 group by o.name, o.description
 order by o.description, o.name$$

CREATE PROCEDURE `asm_translations_p_get_by_text` (IN `p_company_id` INT, IN `p_text` VARCHAR(100) CHARSET utf8mb4)  NO SQL
select o.name, o.description,
       'id||lang||body' rawTranslationsHeader,
       GROUP_CONCAT(distinct concat(COALESCE(r.id,0), '||', l.lang, '||', COALESCE(r.body,'')) ORDER by l.lang SEPARATOR '&&') rawTranslations
  from 
      (select t.name, max(t.description) description, t.company_id
  from asm_common_translations t
 where t.type_id=1 and t.is_active=1 and t.company_id=p_company_id
   and (
       t.name like concat('%',p_text,'%') or
       t.description like concat('%',p_text,'%') or
       t.body like concat('%',p_text,'%')
   )
      group by t.company_id, t.name) o
  join asm_company_languages c on c.company_id=o.company_id
  join asm_languages l on l.id=c.lang_id
  left join asm_common_translations r on r.name=o.name and r.company_id=o.company_id and r.lang=l.lang and r.is_active=1
 group by o.name, o.description
 order by o.description, o.name$$

CREATE PROCEDURE `asm_translations_p_get_items` (IN `p_company_id` INT, IN `p_page` INT, IN `p_limit` INT)  NO SQL
select o.name, o.description, 
       'id||lang||body' rawTranslationsHeader,
       GROUP_CONCAT(distinct concat(COALESCE(r.id,0), '||', l.lang, '||', COALESCE(r.body,'')) ORDER by l.lang SEPARATOR '&&') rawTranslations
  from 
      (select t.company_id, t.name, max(t.description) description
         from asm_common_translations t
        where t.company_id=p_company_id and t.is_active=1 and t.type_id=1
