CREATE PROCEDURE `sitep_get_entities_by_type` (IN `p_company_id` INT, IN `p_type` VARCHAR(100), IN `p_lang` VARCHAR(10), IN `p_start` INT, IN `p_limit` INT)  NO SQL
select e.id, e.show_date showDate, e.main_image mainImage, e.main_image_type mainImageType, e.main_image_noext mainImageNoExt,
       d.url_name urlName, d.title, d.short_description shortDescription
  from asm_entity_types t
  join asm_entities e on e.company_id=t.company_id and e.type_id=t.id and e.is_visible=1 and e.is_active=1 and (t.has_archive=0 or e.is_archive=0 and p_type not like '%archive' or e.is_archive=1 and  p_type like '%archive')
  join asm_entity_details d on d.entity_id=e.id and d.lang=p_lang
 where t.name=replace(p_type,'-archive','') and t.company_id=p_company_id and t.is_visible=1 and t.is_active=1
 order by e.show_date desc, e.id desc
 limit p_start, p_limit$$

CREATE PROCEDURE `sitep_get_entity_by_id` (IN `p_company_id` INT, IN `p_entity_id` INT, IN `p_lang` VARCHAR(10))  NO SQL
select e.id, case when t.has_date=1 then e.show_date else null end entityDate, e.main_image mainImage, e.main_image_type mainImageType, e.main_image_noext mainImageNoExt,
       d.seo_keywords seoKeywords, d.seo_description seoDescription, d.title, d.body, d.short_description shortDescription, t.name typeName, concat('article/', e.id, '-', d.url_name, '.html') urlName,
       'id||imageFile||imageType||imageFileNoExt' rawImagesHeader,
       GROUP_CONCAT(distinct concat(i.id, '||', i.image_file, '||', i.image_type, '||', i.image_file_noext) ORDER by i.sort_order SEPARATOR '&&') rawImages
  from asm_entities e
  left join asm_entity_types t on e.type_id=t.id and e.company_id=t.company_id
  left join asm_entity_details d on d.entity_id=e.id and d.lang=p_lang
  left join asm_entity_images i on i.entity_id = e.id and i.is_visible=1 and i.is_active=1
 where e.company_id=p_company_id and e.id=p_entity_id and e.is_visible=1 and e.is_active=1
 group by e.id, t.has_date, e.show_date, e.main_image, e.main_image_type, e.main_image_noext,
       d.seo_keywords, d.seo_description, d.title, d.body, d.short_description, t.name, d.url_name$$

CREATE PROCEDURE `sitep_get_entity_type_details` (IN `p_company_id` INT, IN `p_entity` VARCHAR(100), IN `p_lang` VARCHAR(10))  NO SQL
select t.id, t.name typeName, t.main_image mainImage, t.main_image_type mainImageType, t.main_image_noext mainImageNoExt,
       d.h1_title h1Title, d.title, d.body, d.short_description shortDescription, concat(t.name, '.html') urlName, 
       d.seo_keywords seoKeywords, d.seo_description seoDescription,
       count(e.id) entityCount
  from asm_entity_types t
  left join asm_entity_type_details d on d.type_id=t.id and d.lang=p_lang
  left join asm_entities e on e.type_id=t.id and e.company_id=t.company_id and e.is_visible=1 and e.is_active=1
  and (t.has_archive=0 or e.is_archive=0 and p_entity not like '%archive' or e.is_archive=1 and  p_entity like '%archive')
 where t.name=replace(p_entity,'-archive','') and t.company_id=p_company_id and t.is_visible=1 and t.is_active=1
 group by t.id, t.name, t.main_image, t.main_image_type, t.main_image_noext, d.h1_title,
       d.title, d.body, d.short_description, 
       d.seo_keywords, d.seo_description$$

CREATE PROCEDURE `sitep_get_initial_objects` (IN `p_company` INT, IN `p_lang` VARCHAR(10))  NO SQL
BEGIN
SET SESSION group_concat_max_len = 1000000;

SELECT id, name, body, description FROM asm_common_translations where is_active=1 and company_id=p_company and type_id=1 and lang=p_lang;

SELECT id, name, body, description FROM asm_common_translations where is_active=1 and company_id=p_company and type_id in (2,3) and lang='';

SELECT pm.id, pm.name, pm.link, pd.title,
       'id||name||link||title' rawChildrenHeader,
       GROUP_CONCAT(
           concat(cm.id, '||', cm.name, '||', cm.link, '||', cd.title)
           order by cm.menu_order SEPARATOR '&&' ) rawChildren
  FROM asm_menu pm
  left join asm_object_details pd on pm.id=pd.object_id and pd.object_type=1 and pd.lang=p_lang
  left join asm_menu cm on cm.parent_id = pm.id
  left join asm_object_details cd on cm.id=cd.object_id and cd.object_type=1 and cd.lang=p_lang
 where pm.parent_id is null and pm.company_id=p_company and pm.is_active=1
 group by pm.id, pm.name, pm.link, pd.title
 order by pm.menu_order;

SELECT e.id, t.id entityTypeId, t.name entityType, e.main_image mainImage, e.main_image_type mainImageType, e.main_image_noext mainImageNoExt,
       d.link, d.url_name urlName, d.seo_keywords seoKeywords, d.seo_description seoDescription,
       d.title, d.short_description shortDescription, d.body
  FROM asm_entity_types t
  join asm_entities e on t.company_id=p_company and t.company_id=e.company_id and t.id=e.type_id and t.is_visible=1 and t.is_active=1 and e.is_important=1
  left join asm_entity_details d on d.entity_id=e.id and d.lang=p_lang;

END$$

CREATE PROCEDURE `sysp_change_password` (IN `p_username` VARCHAR(100), IN `p_password` VARCHAR(100), IN `p_new_password` VARCHAR(100))  NO SQL
BEGIN
  DECLARE l_id int;
  
  SELECT max(u.id)
    into l_id
    from asm_sys_users u 
   where u.company_id=1 
     and u.username=p_username
     and u.password = sha1(p_password);
     
  if l_id > 0 then 
    update asm_sys_users 
       set password=sha1(p_new_password)
     where id=l_id;  
    
    update asm_sys_sessions s 
       set s.is_active=0
     where s.user_id=l_id
       and s.is_active=1;
  end if;
  
  select l_id id;
END$$

CREATE PROCEDURE `sysp_login` (IN `p_username` VARCHAR(100), IN `p_password` VARCHAR(100))  NO SQL
BEGIN
  DECLARE l_id int;
  DECLARE l_logo varchar(255);
  DECLARE l_api_url varchar(255);
  DECLARE l_items int;
  DECLARE l_site_url varchar(255);
  
  
  #get id
  select u.id, c.logo, c.api_url, c.items_per_page, c.site_url 
    into l_id, l_logo, l_api_url, l_items, l_site_url
    from asm_sys_users u
    join asm_company_customers c on c.id=u.company_id and c.is_active=1
   where u.username=p_username and u.password=sha1(p_password)
     and u.is_enabled=1 and u.is_active=1;
   
  if l_id is null then 
    #user not found, return error
    SIGNAL SQLSTATE '21020'
       SET MESSAGE_TEXT = 'USER_NOT_FOUND', MYSQL_ERRNO = 21020;
  else
    #user found
    
    #deactivate all sessions
    update asm_sys_sessions set is_active=0 where user_id=l_id;
    
    #generating new session key
    set @l_session := sha1(concat(CURRENT_TIMESTAMP, l_id, p_username, rand()));
    
    values (l_id, @l_session);
    
    #return result
    select last_insert_id() newId, @l_session sessionKey,
           l_logo logo, l_api_url apiUrl, 
           l_items itemLimit, l_site_url siteUrl;
  end if;
END$$

CREATE PROCEDURE `sysp_users_get_id_by_session` (IN `p_session_key` VARCHAR(100))  NO SQL
BEGIN
  DECLARE l_id int;
  
  select s.user_id
    into l_id
    from asm_sys_sessions s
    join asm_sys_users u on s.user_id=u.id and u.is_enabled=1 and u.is_active=1
    join asm_company_customers c on c.id=u.company_id and c.is_active=1
   where s.session_key=p_session_key and s.is_active=1;
   
  if l_id is null THEN
    SIGNAL SQLSTATE '21021'
       SET MESSAGE_TEXT = 'NOT_ACTIVE_SESSION', MYSQL_ERRNO = 21021;
  end if;
  
  
  select u.id, u.username, u.company_id companyId, u.lang, c.logo,
         c.site_url siteUrl, c.api_url wsUrl, 
         c.items_per_page itemLimit
    from asm_sys_users u 
    join asm_company_customers c on c.id=u.company_id and c.is_active=1
   where u.id=l_id and u.is_enabled=1 and u.is_active=1;
  
END$$

CREATE PROCEDURE `sysp_users_get_modules` (IN `p_user_id` INT)  NO SQL
select s.id, m.name module, s.name subModule, s.description
  from asm_sys_user_accesses a
  join asm_sys_modules m on a.user_id=p_user_id and a.is_active=1 and a.module_id=m.id
  join asm_sys_submodules s on s.module_id=m.id and s.id=a.submodule_id$$

CREATE FUNCTION `asm_f_extract_string` (`i` SMALLINT UNSIGNED, `strlist` TEXT, `sep` VARCHAR(5)) RETURNS TEXT CHARSET utf8mb4 NO SQL
    DETERMINISTIC
return NULLIF(SUBSTRING_INDEX(SUBSTRING_INDEX(CONCAT(0b0, sep, strlist, sep, 0b0), sep, i+1.5*(SIGN(i+0.5)+1)-1), sep, -SIGN(i+0.5)),0b0)$$

CREATE FUNCTION `asm_f_remove_file_ext` (`p_text` VARCHAR(255)) RETURNS VARCHAR(255) CHARSET utf8mb4 NO SQL
return REVERSE(SUBSTRING(REVERSE(p_text), instr(REVERSE(p_text), '.') + 1, 999))$$

CREATE FUNCTION `asm_f_substring_count` (`strlist` TEXT, `sep` VARCHAR(5)) RETURNS INT(11) NO SQL
    DETERMINISTIC
return  1+(CHAR_LENGTH(strlist)-CHAR_LENGTH(REPLACE(strlist,sep,'')))/char_length(sep)$$

CREATE FUNCTION `sysf_users_get_id_by_session` (`p_session_key` VARCHAR(100)) RETURNS INT(11) NO SQL
BEGIN
  DECLARE l_id int;
  
  select s.user_id
    into l_id
    from asm_sys_sessions s
    join asm_sys_users u on s.user_id=u.id and u.is_enabled=1 and u.is_active=1
   where s.session_key=p_session_key and s.is_active=1;
   
  if l_id is null THEN
    SIGNAL SQLSTATE '21021'
       SET MESSAGE_TEXT = 'NOT_ACTIVE_SESSION', MYSQL_ERRNO = 21021;
  end if;
  
  return l_id;
END$$

